          setFeedbackMessage('Time removido com sucesso')
          setFeedbackIsOpen(true)
          setTimeout(() => setFeedbackIsOpen(false), 3000)
          getAllTeams()
          return
        }
        setOpen(false)
        setFeedbackColor('fail')
        setFeedbackMessage(response.message)
        setFeedbackIsOpen(true)
        setTimeout(() => setFeedbackIsOpen(false), 3000)
        return
      }
    })
