import React, { useState, useEffect } from "react";
// reactstrap components
import { Card, CardHeader, CardBody, Row, Col, Button, FormGroup, Label, Input, Modal, ModalBody, Alert } from "reactstrap";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMinusCircle } from '@fortawesome/free-solid-svg-icons'
import Swal from 'sweetalert2'

import axios from 'axios'

const baseUrl = 'http://localhost:5000' //process.env.BASE_URL_API

function Equipes() {
  const [open, setOpen] = useState(false);
  const [focusAfterClose, setFocusAfterClose] = useState(true);
  const [teams, updateTeams] = useState([]);
  const [colors, updateColors] = useState([]);
  const [color, setColor] = useState([]);
  const [team, setTeam] = useState([]);
  const [feedbackColor, setFeedbackColor] = useState([]);
  const [feedbackMessage, setFeedbackMessage] = useState([]);
  const [feedbackIsOpen, setFeedbackIsOpen] = useState([]);




  const getAllTeams = async () => {// buscar todas as Equipes
    try {
      const allTeams = await fetch(baseUrl+'/teams', { method: 'GET' })
      const response = await allTeams.json()
      updateTeams(response.data)
    } catch (error) {
      console.log(error)
    }


  }

  const getAllCollors = async () => {// buscar todas as Equipes
    const allColors = await fetch(baseUrl+'/colors', { method: 'GET' })
    const response = await allColors.json()
    updateColors(response.data)

  }


  const createTeam = async (arg1, arg2) => {
    console.log('Execultou criar')
    const data = {
      name: arg1,
      color_id: arg2
    }
    const team = await axios.post(baseUrl+'/team', data)
    const response = await team.data
    if(response.success){
      setOpen(false)
      setFeedbackColor('success')
      setFeedbackMessage('Seu time foi criado com sucesso')
      setFeedbackIsOpen(true)
      setTimeout(() => setFeedbackIsOpen(false), 3000)
      getAllTeams()
    }
  }

  const deleteTeam = id => {

    Swal.fire({
      title: 'Deseja excluir esse time?',
      showCancelButton: true,
      confirmButtonText: 'Excluir <i class="fas fa-backspace"></i>',
      cancelButtonText:'NÃ£o'
    }).then((result) => {
      if(result.isConfirmed)
        console.log('Exluiu')
      })
  }

  const toggle = () => setOpen(!open);
  // eslint-disable-next-line
  const handleSelectChange = ({target: { value }}) => {
      setFocusAfterClose(JSON.parse(value));
  }




  useEffect(()=>{
    getAllTeams()
    getAllCollors()
  },[])



  return (
    <>
      <div className="content">
        <Row>
          <Col md="12">
            <Card>
              <CardHeader>
                <h1 className="title">Equipes</h1>
                <p className="category">
                Neste mÃ³dulo vocÃª pode criar e gerenciar equipes
                </p>
              </CardHeader>
              <CardBody className="all-icons">
              <Alert 
              color={feedbackColor}
              isOpen={feedbackIsOpen}
              >
                {feedbackMessage}
              </Alert>
                <Row>
                  <Col
                  className="col-xs-6 col-xs-6"
                  lg="4"
                  md="2"
                  sm="4">
                    <Button
                    onClick={()=>setOpen(true)}
                    >
                      <div>
                        <i className="tim-icons icon-simple-add"/>
                        <p>Criar nova</p>
                      </div>
                    </Button>
                  </Col>
                </Row>
                <Row>
                    <Modal returnFocusAfterClose={focusAfterClose} isOpen={open}>
                      <div className="modal-header">
                        <h5 className="modal-title" id="exampleModalLabel">
                          Preencha todos os campos
                        </h5>
                          <button
                          type="button"
                          className="close"
                          data-dismiss="modal"
                          aria-hidden="true"
                          onClick={toggle}
                        >
                          <i className="tim-icons icon-simple-remove" />
                        </button>
                      </div>
                      <ModalBody>
                        <form>
                          <FormGroup>
                            <Label for="exampleEmail1">Nome</Label>
                            <Input
                              type="text"
                              name="team"
                              id="exampleEmai1l"
                              placeholder="Digite o nome da equipe..."
                              className="text-dark"
                              onChange={(e)=>setTeam(e.target.value)}
                            />
                          </FormGroup>
                          <FormGroup>
                            <Label for="exampleSelect1">Cor</Label>
                            <Input type="select" name="color" className="text-dark" id="exampleSelect1" onChange={(e)=>setColor(e.target.value)}>
                              <option value="0">Selecione uma cor...</option>
                              {colors.map(color => <option value={color.id}>{color.ds_color}</option>)}
                            </Input>
                          </FormGroup>
                          <Button color="primary" onClick={()=>{createTeam(team, color)}}>
                            Criar
                          </Button>
                        </form>
                      </ModalBody>
                  </Modal>
                </Row>
                <Row>
                  {teams.map(team=>{
                    return (

                      <Col
                     className="font-icon-list col-xs-6 col-xs-6"
                     lg="4"
                     md="4"
                     sm="4"
                   >
                     <button
                          type="button"
                          className="close"
                          data-dismiss="modal"
                          aria-hidden="true"
                          style={{marginTop:'18px', marginRight:'5px', color:'#D44638'}}
                          //onClick={toggle}
                        >
                          <FontAwesomeIcon onClick={()=>{deleteTeam(team.id)}} icon={faMinusCircle} />
                        </button>
                     <div className="font-icon-detail" style={{backgroundColor: team.cd_color}}>
                       <h1>{team.name}</h1>
                     </div>
                   </Col>
                    )
                  })
                } 
                </Row>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </> 
  );
}

export default Equipes;
